import React, { useState } from "react";
import "./Lists.css";
import InputForm from "./InputForm";
function Lists() {
  const [todos, setTodos] = useState([]);
  const addTodoHandler = (todo) => {
    setTodos((prevTodo) => {
      return [...prevTodo, todo];
    });
  };
  return (
    <>

      <div className="list">
        <p>begin Promotion</p>
      </div>
      <div className="list">
        <p>Read an article</p>
      </div>
      <div className="list">
        <p>try not to fall asleep</p>
      </div>
      <div className="list">
        <p>watch 'sherlok'</p>
      </div>
      <div className="list">
        <p>begin QA for the product</p>
      </div>
      <div className="list">
        <p>go for a walk</p>
      </div>
      <InputForm onAddTodo={addTodoHandler} />
      {/* <InputForm /> */}
    </>
  );
}

export default Lists;
