import React, { useState } from "react";

function ListItems(props) {
  const [checked, setChecked] = useState(localStorage.getItem(props.index));
  // state handling as per the input:checkbox
  const checkedHandler = (e) => {
    const isNotChecked = e.target.checked;
    if (isNotChecked) {
      setChecked(true);
      localStorage.setItem(props.index.checked, true);
    } else {
      setChecked(false);
      localStorage.setItem(props.index, false);
    }
  };
  return (
    <div className={checked ? "list completed" : "list not-completed"}>
      <p>{props.todo.title}</p>
      <input
        className="list-checkbox"
        type="checkbox"
        onChange={checkedHandler}
      />
    </div>
  );
}

export default ListItems;
