import React, { useState } from "react";

import "./Button.css";
import plus from "../Images/icons8-plus-48.png";

function Button(props) {
  const [showInput, setShowInput] = useState(false);
  const [isClicked, setIsClicked] = useState(false);
  function clickHandler(e) {
    setIsClicked(true);
  }
  const [enteredTodo, setEnteredTodo] = useState([]);
  // on form submission handler
  function submitHandler(e) {
    e.preventDefault();
    // error handling for not giving input
    if (enteredTodo === "") {
      alert("Please enter the todo.");
    } else {
      const defined_todos = {
        id: Math.random().toString(),
        todo: enteredTodo,
      };
      props.onAddTodo(defined_todos);
      setEnteredTodo("");
    }
  }
  return (
    <div className="button__container">
      <form className={isClicked ? "form__container": "form__container-"} onSubmit={submitHandler}>
        <input
          type="text"
          value={enteredTodo}
          onChange={(e) => setEnteredTodo(e.target.value)}
        />
      </form>
      <button className="button__container-btn" onClick={clickHandler}>
        <img src={plus} alt="plus" />
      </button>
    </div>
  );
}

export default Button;
