import React, { useState } from "react";

function ListItems(props) {
  const [checked, setChecked] = useState(
    localStorage.getItem(props.todo.checked)
  );
  // state handling as per the input:checkbox
  const checkedHandler = (e) => {
    const isNotChecked = e.target.checked;

    if (isNotChecked) {
      setChecked(localStorage.setItem(props.todo.checked, true));
      console.log(props);
    } else {
      setChecked(false);
      localStorage.removeItem(props.index);
    }
  };
  return (
    <div className={checked ? "list completed" : "list not-completed"}>
      <p>{props.todo.title}</p>
      <input
        className="list-checkbox"
        type="checkbox"
        onChange={checkedHandler}
      />
    </div>
  );
}

export default ListItems;
