import React, { useState } from "react";
import InputForm from "./InputForm";
import defined_todos from "./ListItems.json";
import "./Lists.css";
function Lists() {
  const [todos, setTodos] = useState(defined_todos);
  const addTodoHandler = (todo) => {
    setTodos((prevTodo) => {
      return [...prevTodo, todo];
    });
  };
  const [checked, setChecked] = useState(false);

  const checkedHandler = (e) => {
    const isListChecked = e.target.checked ? "list" : "list not__completed";
    console.log(e.target.checked);
  };

  const showHandler = () => {};
  return (
    <>
      <div className="list__container">
        {todos.map((todo) => (
          <div key={todo.id} className={"list "}>
            <p>{todo.todo}</p>
            <input type="checkbox" onChange={checkedHandler} />
          </div>
        ))}
      </div>
      {/* {todos.map((todo) => console.log(todo.todo)} */}
      {/* {showInput ? (
        <InputForm onAddTodo={addTodoHandler} showInput={showHandler} />
      ) : (
        <></>
      )} */}
      <InputForm onAddTodo={addTodoHandler} showInput={showHandler} />
    </>
  );
}
export default Lists;
