import React, { useState } from "react";
import "./Lists.css";
import InputForm from "./InputForm";
function Lists() {
  const [todos, setTodos] = useState("");
  function addTodoHandler(todo) {
    setTodos((prevTodo) => {
      return [...prevTodo, todo];
    });
  }
  return (
    <>
      <InputForm onAddTodo={addTodoHandler} />
      {
        todos.locale
      }
      <div className="list">
        <p>Buy new sweatshirt</p>
      </div>
      <div className="list">
        <p>begin Promotion</p>
      </div>
      <div className="list">
        <p>Read an article</p>
      </div>
      <div className="list">
        <p>try not to fall asleep</p>
      </div>
      <div className="list">
        <p>watch 'sherlok'</p>
      </div>
      <div className="list">
        <p>begin QA for the product</p>
      </div>
      <div className="list">
        <p>go for a walk</p>
      </div>
    </>
  );
}

export default Lists;
