import React, { useState } from "react";

function ListItems({ todo }) {
  const [checked, setChecked] = useState(
    localStorage.getItem({ todo: { checked: false } })
  );
  // state handling as per the input:checkbox
  const checkedHandler = (e) => {
    const isNotChecked = e.target.checked;

    if (isNotChecked) {
      setChecked(localStorage.setItem({ todo: { checked: true } }));
      console.log(props);
    } else {
      setChecked(localStorage.removeItem({ todo: { checked: false } }));
      console.log(props);
    }
  };
  return (
    <div className={checked ? "list not-completed" : "list completed"}>
      <p>{props.todo.title}</p>
      <input
        className="list-checkbox"
        type="checkbox"
        onChange={checkedHandler}
      />
    </div>
  );
}

export default ListItems;
